from django import formsfrom .models import Fill_Formclass Fill_FormForm(forms.ModelForm):    gender = forms.ChoiceField(widget=forms.RadioSelect)    materials_provided=forms.MultipleChoiceField(label='Materials Provided',widget=forms.CheckboxSelectMultiple)    class Meta:        model = Fill_Form        fields = ['user_name', 'date_of_birth', 'age', 'gender', 'phone_number', 'mail_id', 'address', 'district', 'branch', 'type_of_account', 'materials_provided']        labels = {            'user_name': 'Name',            'date_of_birth': 'Date of Birth',            'age': 'Age',            'gender': 'Gender',            'phone_number': 'Phone Number',            'mail_id': 'Email',            'address': 'Address',            'district': 'District',            'branch': 'Branch',            'type_of_account': 'Type of Account',            'materials_provided': 'Materials Provided',            }        widgets = {            'user_name': forms.TextInput(attrs={'class': 'form-control', 'placeholder': 'Enter your Name'}),            'date_of_birth': forms.DateInput(attrs={'class': 'form-control','type': 'date'}),            'age': forms.NumberInput(attrs={'class': 'form-control'}),            'phone_number': forms.NumberInput(attrs={'class': 'form-control'}),            'mail_id': forms.EmailInput(attrs={'class': 'form-control'}),            'address': forms.Textarea(attrs={'class': 'form-control'}),            'district': forms.Select(attrs={'class': 'form-control'}),            'branch': forms.Select(attrs={'class': 'form-control'}),            'type_of_account': forms.Select(attrs={'class': 'form-control'}),        }